"""#Data split into training and testing"""

#Using close pricing for prediction 

len_df = len(dataset)
train_data = pd.DataFrame(dataset['Close'][0:int(len_df * 0.7)])
test_data = pd.DataFrame(dataset['Close'][int(len_df *0.7): len_df])

print(train_data.shape)
print(test_data.shape)

"""#Scaling the training data"""

from sklearn.preprocessing import MinMaxScaler

sc = MinMaxScaler(feature_range =(0,1))

train_data_scaled_array = sc.fit_transform(train_data)
train_data_scaled_array

x_train = []
y_train = []
for i in range(100, train_data_scaled_array.shape[0]):
  x_train.append(train_data_scaled_array[i-100:i])
  y_train.append(train_data_scaled_array[i,0])

x_train, y_train = np.array(x_train), np.array(y_train)
print(x_train.shape)

"""#Using LSTM model"""

from keras.layers import Dense, Dropout, LSTM
from keras.models import Sequential

model = Sequential()
model.add(LSTM(units = 50, activation = 'relu', return_sequences= True, input_shape = [x_train.shape[1], 1]))
model.add(Dropout(0.2))  #added first layer 

model.add(LSTM(units = 60, activation = 'relu', return_sequences= True))  #2nd layer and so on
model.add(Dropout(0.3))

model.add(LSTM(units = 80, activation = 'relu', return_sequences= True))
model.add(Dropout(0.4))

model.add(LSTM(units = 120, activation = 'relu'))
model.add(Dropout(0.4))

model.add(Dense(units = 1))

model.summary()

model.compile(optimizer='adam', loss= 'mean_squared_error')
model.fit(x_train,y_train,epochs = 50)

model.save('Keras_model.h5')   #h5 saves the model into HDF5 file format

