"""#Using SVM"""

df = dataset[['Adj Close']]
df[['Adj Close']].tail()

#Create one more column Prediction shifted 10 days up. 
df['Prediction'] = df[['Adj Close']].shift(-10)
#print data set
print(df)

#Create a data set X and convert it into numpy array , which will be having actual values
data = np.array(df.drop(['Prediction'],1))
#Remove the last 10 rows
data = data[:-10]
print(data)

# Create a dataset y which will be having Predicted values and convert into numpy array
data_prediction = np.array(df['Prediction'])
# Remove Last 10 rows
data_prediction = data_prediction[:-10]
print(data_prediction)

# Split the data into train and test with 90 & 10 % respectively
x_train, x_test, y_train, y_test = train_test_split(data, data_prediction, test_size=0.1)

# SVM Model
from sklearn.svm import SVR
svr = SVR(kernel='rbf', C=1e3, gamma=0.1)
# Train the model 
svr.fit(x_train, y_train)

svm_confidence = svr.score(x_test, y_test)
print("SVM Confidence: ", svm_confidence)

prediction_rolling = np.array(df.drop(['Prediction'],1))[-10:]
print(prediction_rolling)

# support vector model predictions for the next ‘15’ days
svm_prediction = svr.predict(prediction_rolling)
print(svm_prediction)

svm_prediction = svm_prediction.tolist()
prediction_rolling = prediction_rolling.tolist()
print(svm_prediction)
print(prediction_rolling )

loss = 0
